    public ArrayList<Store> storeListSelect(int addNum, int typeNum) {
        SQLiteDatabase db = getReadableDatabase();
        String result = "";
        String str1=null, str2=null, str3=null;
        ArrayList<Store> storeList = new ArrayList<Store>();


        if(addNum!=0)
            str1 = "address1= '"+addressList[addNum]+"'";
        else
            str1 = "1= 1";

        if(typeNum!=0)
            str2 = "type= '"+typeList[typeNum]+"'";
        else
            str2 ="1= 1";

        // DB에 있는 데이터를 쉽게 처리하기 위해 Cursor를 사용하여 테이블에 있는 모든 데이터 출력
        String sql = "SELECT * FROM STORE where "+str1+" and "+str2;
        Cursor cursor = db.rawQuery(sql, null);

        while (cursor.moveToNext()) {
            Store store = new Store( );
            store.setStore_id(cursor.getString(0));
            store.setName(cursor.getString(1));
            store.setType(cursor.getString(2));
            store.setAddress1(cursor.getString(3));
            store.setAddress2(cursor.getString(4));
            store.setAddress3(cursor.getString(5));
            store.setTel(cursor.getString(6));
            storeList.add(store);

        }
        db.close();
        return storeList;
    }

    public ArrayList<Usage> usageListSelect(int monthNum) {
        SQLiteDatabase db = getReadableDatabase();
        String result = "";
        String str1=null, str2=null, str3=null;
        ArrayList<Usage> usagesList = new ArrayList<Usage>();


        // DB에 있는 데이터를 쉽게 처리하기 위해 Cursor를 사용하여 테이블에 있는 모든 데이터 출력
        String sql = "SELECT * FROM Usage where month= '"+monthNum+"'";
        Cursor cursor = db.rawQuery(sql, null);

        while (cursor.moveToNext()) {
            Usage usage = new Usage( );
            usage.setStoreName(cursor.getString(1));
            String str = "";
            str = cursor.getString(2) +"-" + cursor.getString(3) +"-"+cursor.getString(4);
            usage.setDate(str);
            usage.setPrice(cursor.getInt(5)+"");
            usagesList.add(usage);
        }
        return usagesList;
    }

    public User getUserInfo() {
        SQLiteDatabase db = getReadableDatabase();
        String sql = "SELECT * FROM USER";
        Cursor cursor = db.rawQuery(sql, null);

        User user = new User();
        user.setName("사용자");
        user.setAddress("1");
        user.setType("1");
        user.setCount("1");
        String str = "";
        //        db.execSQL("insert into USER values('user','익명', '관악구', '1', '1')");
        while (cursor.moveToNext()) {
            user.setName(cursor.getString(1));
            user.setAddress(cursor.getString(2));
            user.setType(cursor.getString(3));
            user.setCount(cursor.getString(4));
        }
        return user;
    }


    public void setUserData(String name , String address, String type, String count) {
        SQLiteDatabase db = getReadableDatabase();
        db.execSQL("UPDATE USER SET name = '"+name+ "', address1='"+address+"', type='"+type+"', count='"+count+"'");
        db.close();
    }

    public void  insertUsageData(String name, String year, String month, String day, String price) {
        SQLiteDatabase db = getReadableDatabase();
        int temp = Integer.parseInt(price);
        db.execSQL("insert into USAGE values(null, '"+name+"', '"+year+"', '"+month+"', '"+day+"', "+temp+")");
        db.close();
    }

